import React, { useState } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';
import axios from 'axios'; // Added for saving changes
import toast from 'react-hot-toast'; // Added for toast notifications

const defaultConnectionOptions = [
  'Friend of the Bride',
  'Friend of the Groom',
  'Family of the Bride',
  'Family of the Groom',
  'College Friend',
  'Work Colleague',
  'Neighbor',
  'Other'
];

const CustomizeWeddingPage = () => {
  const { currentWedding, setCurrentWedding } = useAuth();
  const navigate = useNavigate();
  const [form, setForm] = useState({
    brideName: currentWedding?.brideName || '',
    groomName: currentWedding?.groomName || '',
    welcomeMessage: currentWedding?.welcomeMessage || '',
    weddingDate: currentWedding?.weddingDate || '',
    ceremonyTime: currentWedding?.ceremonyTime || '',
    venue: currentWedding?.venue || '',
    venueAddress: currentWedding?.venueAddress || '',
    language: currentWedding?.language || 'en',
    connectionOptions: currentWedding?.connectionOptions?.length > 0 ? currentWedding.connectionOptions : [
      'Friend of the Bride',
      'Friend of the Groom',
      'Family of the Bride',
      'Family of the Groom'
    ],
  });
  const [activeTab, setActiveTab] = useState('form');
  const [newConnection, setNewConnection] = useState('');

  // Add state for visual style customization
  const [visualStyle, setVisualStyle] = useState({
    colors: {
      primary: currentWedding?.colors?.primary || '#8B5CF6',
      background: currentWedding?.colors?.background || '#FFFFFF',
      text: currentWedding?.colors?.text || '#1F2937'
    },
    font: currentWedding?.fonts?.main || 'Inter',
    countdownStyle: currentWedding?.countdownStyle || 'digital'
  });

  const [saving, setSaving] = useState(false);

  const handleChange = (field, value) => {
    setForm(prev => ({ ...prev, [field]: value }));
  };

  const handleRemoveConnection = (option) => {
    setForm(prev => ({
      ...prev,
      connectionOptions: prev.connectionOptions.filter(o => o !== option)
    }));
  };

  const handleAddConnection = () => {
    if (newConnection.trim() && !form.connectionOptions.includes(newConnection.trim())) {
      setForm(prev => ({
        ...prev,
        connectionOptions: [...prev.connectionOptions, newConnection.trim()]
      }));
      setNewConnection('');
    }
  };

  const handleVisualChange = (field, value) => {
    setVisualStyle(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleColorChange = (colorType, value) => {
    setVisualStyle(prev => ({
      ...prev,
      colors: {
        ...prev.colors,
        [colorType]: value
      }
    }));
  };

  const handleSave = async () => {
    setSaving(true);
    try {
      // Debug: Check if currentWedding exists
      if (!currentWedding) {
        console.error('No current wedding found');
        toast.error('No wedding selected');
        return;
      }

      if (!currentWedding.weddingId) {
        console.error('No wedding ID found:', currentWedding);
        toast.error('Invalid wedding data');
        return;
      }

      const updatedData = {
        ...form,
        colors: visualStyle.colors,
        fonts: { main: visualStyle.font },
        countdownStyle: visualStyle.countdownStyle
      };

      console.log('Saving data:', updatedData);
      console.log('Wedding ID:', currentWedding.weddingId);
      console.log('Current wedding:', currentWedding);

      const response = await axios.put(`/api/weddings/${currentWedding.weddingId}`, updatedData);
      console.log('Save response:', response.data);

      // Update the current wedding in context with the new data
      const updatedWedding = {
        ...currentWedding,
        ...updatedData
      };
      setCurrentWedding(updatedWedding);
      localStorage.setItem('currentWedding', JSON.stringify(updatedWedding));

      toast.success('Changes saved successfully!');
    } catch (error) {
      console.error('Save error:', error);
      console.error('Error response:', error.response?.data);
      toast.error(`Failed to save changes: ${error.response?.data?.error || error.message}`);
    } finally {
      setSaving(false);
    }
  };

  return (
    <div className="min-h-screen p-8 bg-gray-50">
      <div className="max-w-3xl mx-auto bg-white rounded-lg shadow-lg p-8">
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-3xl font-bold text-center flex-1">Customize Your RSVP Form</h1>
          <button className="btn-secondary ml-4" onClick={() => navigate('/dashboard')}>
            Back to Dashboard
          </button>
        </div>
        {/* Single Accordion/Dropdown for RSVP Customization */}
        <div className="mb-8">
          <button className="w-full text-left py-3 px-4 bg-gray-100 rounded-t-lg font-semibold" onClick={() => setActiveTab(activeTab ? '' : 'form')}>
            RSVP Customization
          </button>
          {activeTab && (
            <div className="border border-t-0 rounded-b-lg p-4">
              {/* Tabs/Buttons for navigation */}
              <div className="flex space-x-4 mb-6">
                <button className={`px-4 py-2 rounded ${activeTab === 'form' ? 'bg-purple-100 text-purple-700 font-bold' : 'bg-gray-100 text-gray-700'}`} onClick={() => setActiveTab('form')}>Form Settings</button>
                <button className={`px-4 py-2 rounded ${activeTab === 'connections' ? 'bg-purple-100 text-purple-700 font-bold' : 'bg-gray-100 text-gray-700'}`} onClick={() => setActiveTab('connections')}>Connection Options</button>
                <button className={`px-4 py-2 rounded ${activeTab === 'style' ? 'bg-purple-100 text-purple-700 font-bold' : 'bg-gray-100 text-gray-700'}`} onClick={() => setActiveTab('style')}>Visual Style</button>
              </div>
              {/* Tab Content */}
              {activeTab === 'form' && (
                <div className="space-y-4">
                  <div>
                    <label className="form-label">Bride's Name</label>
                    <input
                      type="text"
                      className="form-input"
                      value={form.brideName}
                      onChange={e => handleChange('brideName', e.target.value)}
                      placeholder="Enter bride's name"
                    />
                  </div>
                  <div>
                    <label className="form-label">Groom's Name</label>
                    <input
                      type="text"
                      className="form-input"
                      value={form.groomName}
                      onChange={e => handleChange('groomName', e.target.value)}
                      placeholder="Enter groom's name"
                    />
                  </div>
                  <div>
                    <label className="form-label">Welcome Sentence</label>
                    <textarea
                      className="form-input"
                      value={form.welcomeMessage}
                      onChange={e => handleChange('welcomeMessage', e.target.value)}
                      placeholder="Enter a welcome message"
                    />
                  </div>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                      <label className="form-label">Wedding Date</label>
                      <input
                        type="date"
                        className="form-input"
                        value={form.weddingDate}
                        onChange={e => handleChange('weddingDate', e.target.value)}
                      />
                    </div>
                    <div>
                      <label className="form-label">Ceremony Time</label>
                      <input
                        type="time"
                        className="form-input"
                        value={form.ceremonyTime}
                        onChange={e => handleChange('ceremonyTime', e.target.value)}
                      />
                    </div>
                  </div>
                  <div>
                    <label className="form-label">Venue Name</label>
                    <input
                      type="text"
                      className="form-input"
                      value={form.venue}
                      onChange={e => handleChange('venue', e.target.value)}
                      placeholder="Enter venue name"
                    />
                  </div>
                  <div>
                    <label className="form-label">Venue Address</label>
                    <textarea
                      className="form-input"
                      value={form.venueAddress}
                      onChange={e => handleChange('venueAddress', e.target.value)}
                      placeholder="Enter venue address"
                      rows="2"
                    />
                  </div>
                  <div>
                    <label className="form-label">Primary Language</label>
                    <select
                      className="form-select"
                      value={form.language}
                      onChange={e => handleChange('language', e.target.value)}
                    >
                      <option value="en">English 🇺🇸</option>
                      <option value="he">Hebrew 🇮🇱</option>
                    </select>
                  </div>
                </div>
              )}
              {activeTab === 'connections' && (
                <div>
                  <div className="mb-4">
                    <label className="form-label">Connection Options</label>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                      {form.connectionOptions.map(option => (
                        <div key={option} className="flex items-center justify-between">
                          <span className="text-sm text-gray-700">{option}</span>
                          <button type="button" className="text-red-500 ml-2" onClick={() => handleRemoveConnection(option)}>Remove</button>
                        </div>
                      ))}
                    </div>
                  </div>
                  <div className="flex space-x-2 mt-4">
                    <input
                      type="text"
                      className="form-input flex-1"
                      value={newConnection}
                      onChange={e => setNewConnection(e.target.value)}
                      placeholder="Add new connection option"
                    />
                    <button type="button" className="btn-secondary" onClick={handleAddConnection}>Add</button>
                  </div>
                </div>
              )}
              {activeTab === 'style' && (
                <div className="space-y-6">
                  {/* Theme Colors */}
                  <div>
                    <div className="flex justify-between items-center mb-4">
                      <h3 className="text-lg font-semibold">Theme Colors</h3>
                      <button 
                        className="text-sm text-purple-600 hover:text-purple-800"
                        onClick={() => {
                          setVisualStyle(prev => ({
                            ...prev,
                            colors: {
                              primary: '#8B5CF6',
                              background: '#FFFFFF',
                              text: '#1F2937'
                            }
                          }));
                        }}
                      >
                        Reset to Default
                      </button>
                    </div>
                    <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                      <div>
                        <label className="form-label">Primary Color</label>
                        <div className="flex items-center space-x-2">
                          <input
                            type="color"
                            className="w-12 h-10 rounded border"
                            value={visualStyle.colors.primary}
                            onChange={e => handleColorChange('primary', e.target.value)}
                          />
                          <input
                            type="text"
                            className="flex-1 px-3 py-2 border rounded-md text-sm"
                            value={visualStyle.colors.primary}
                            onChange={e => handleColorChange('primary', e.target.value)}
                            placeholder="#8B5CF6"
                          />
                        </div>
                        <p className="text-xs text-gray-500 mt-1">Used for buttons, accents, and highlights</p>
                      </div>
                      <div>
                        <label className="form-label">Background Color</label>
                        <div className="flex items-center space-x-2">
                          <input
                            type="color"
                            className="w-12 h-10 rounded border"
                            value={visualStyle.colors.background}
                            onChange={e => handleColorChange('background', e.target.value)}
                          />
                          <input
                            type="text"
                            className="flex-1 px-3 py-2 border rounded-md text-sm"
                            value={visualStyle.colors.background}
                            onChange={e => handleColorChange('background', e.target.value)}
                            placeholder="#FFFFFF"
                          />
                        </div>
                        <p className="text-xs text-gray-500 mt-1">Main background color of the form</p>
                      </div>
                      <div>
                        <label className="form-label">Text Color</label>
                        <div className="flex items-center space-x-2">
                          <input
                            type="color"
                            className="w-12 h-10 rounded border"
                            value={visualStyle.colors.text}
                            onChange={e => handleColorChange('text', e.target.value)}
                          />
                          <input
                            type="text"
                            className="flex-1 px-3 py-2 border rounded-md text-sm"
                            value={visualStyle.colors.text}
                            onChange={e => handleColorChange('text', e.target.value)}
                            placeholder="#1F2937"
                          />
                        </div>
                        <p className="text-xs text-gray-500 mt-1">Color for all text content</p>
                      </div>
                    </div>
                  </div>
                  {/* Font Options */}
                  <div>
                    <div className="flex justify-between items-center mb-4">
                      <h3 className="text-lg font-semibold">Font Options</h3>
                      <button 
                        className="text-sm text-purple-600 hover:text-purple-800"
                        onClick={() => {
                          setVisualStyle(prev => ({
                            ...prev,
                            font: 'Inter'
                          }));
                        }}
                      >
                        Reset to Default
                      </button>
                    </div>
                    <select
                      className="form-select"
                      value={visualStyle.font}
                      onChange={e => handleVisualChange('font', e.target.value)}
                    >
                      <option value="Inter">Inter - Modern & Clean</option>
                      <option value="Playfair Display">Playfair Display - Elegant Serif</option>
                      <option value="Roboto">Roboto - Professional</option>
                      <option value="Dancing Script">Dancing Script - Handwritten</option>
                      <option value="Montserrat">Montserrat - Contemporary</option>
                    </select>
                    <div className="mt-2 p-3 bg-gray-50 rounded" style={{ fontFamily: visualStyle.font }}>
                      <p className="text-sm">Preview: This is how your text will look with {visualStyle.font}</p>
                    </div>
                  </div>
                  {/* Countdown Clock Styles */}
                  <div>
                    <h3 className="text-lg font-semibold mb-4">Countdown Clock Style</h3>
                    <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                      <label className="flex items-center p-3 border rounded cursor-pointer">
                        <input
                          type="radio"
                          name="countdownStyle"
                          value="digital"
                          checked={visualStyle.countdownStyle === 'digital'}
                          onChange={e => handleVisualChange('countdownStyle', e.target.value)}
                          className="mr-2"
                        />
                        <div>
                          <div className="font-semibold">Digital</div>
                          <div className="text-xs text-gray-500">Modern digital display</div>
                        </div>
                      </label>
                      <label className="flex items-center p-3 border rounded cursor-pointer">
                        <input
                          type="radio"
                          name="countdownStyle"
                          value="circles"
                          checked={visualStyle.countdownStyle === 'circles'}
                          onChange={e => handleVisualChange('countdownStyle', e.target.value)}
                          className="mr-2"
                        />
                        <div>
                          <div className="font-semibold">Circles</div>
                          <div className="text-xs text-gray-500">Elegant circular design</div>
                        </div>
                      </label>
                      <label className="flex items-center p-3 border rounded cursor-pointer">
                        <input
                          type="radio"
                          name="countdownStyle"
                          value="elegant"
                          checked={visualStyle.countdownStyle === 'elegant'}
                          onChange={e => handleVisualChange('countdownStyle', e.target.value)}
                          className="mr-2"
                        />
                        <div>
                          <div className="font-semibold">Elegant Lines</div>
                          <div className="text-xs text-gray-500">Sophisticated line design</div>
                        </div>
                      </label>
                    </div>

                    {/* Countdown Preview */}
                    <div className="mt-4 p-4 bg-gray-50 rounded-lg">
                      <h4 className="text-sm font-medium mb-2" style={{ color: visualStyle.colors.text, fontFamily: visualStyle.font }}>
                        Countdown Preview:
                      </h4>
                      <div className="text-center">
                        {visualStyle.countdownStyle === 'digital' && (
                          <div className="grid grid-cols-4 gap-2">
                            {['365', '24', '60', '30'].map((value, index) => (
                              <div key={index} className="bg-white p-2 rounded border" style={{ borderColor: visualStyle.colors.primary }}>
                                <div className="text-lg font-bold" style={{ color: visualStyle.colors.primary, fontFamily: visualStyle.font }}>{value}</div>
                                <div className="text-xs" style={{ color: visualStyle.colors.text, fontFamily: visualStyle.font }}>
                                  {['Days', 'Hours', 'Minutes', 'Seconds'][index]}
                                </div>
                              </div>
                            ))}
                          </div>
                        )}
                        {visualStyle.countdownStyle === 'circles' && (
                          <div className="flex justify-center space-x-4">
                            {['365', '24', '60', '30'].map((value, index) => (
                              <div key={index} className="text-center">
                                <div className="w-16 h-16 rounded-full border-4 flex items-center justify-center mx-auto" 
                                     style={{ borderColor: visualStyle.colors.primary }}>
                                  <div className="text-sm font-bold" style={{ color: visualStyle.colors.primary, fontFamily: visualStyle.font }}>{value}</div>
                                </div>
                                <div className="text-xs mt-1" style={{ color: visualStyle.colors.text, fontFamily: visualStyle.font }}>
                                  {['Days', 'Hours', 'Minutes', 'Seconds'][index]}
                                </div>
                              </div>
                            ))}
                          </div>
                        )}
                        {visualStyle.countdownStyle === 'elegant' && (
                          <div className="space-y-2">
                            {['365 Days', '24 Hours', '60 Minutes', '30 Seconds'].map((text, index) => (
                              <div key={index} className="flex items-center justify-between p-2 border-b" style={{ borderColor: visualStyle.colors.primary }}>
                                <span className="text-sm" style={{ color: visualStyle.colors.text, fontFamily: visualStyle.font }}>{text}</span>
                                <span className="text-lg font-bold" style={{ color: visualStyle.colors.primary, fontFamily: visualStyle.font }}>
                                  {['365', '24', '60', '30'][index]}
                                </span>
                              </div>
                            ))}
                          </div>
                        )}
                      </div>
                    </div>
                  </div>
                  {/* Live Preview */}
                  <div>
                    <h3 className="text-lg font-semibold mb-4">Live Preview</h3>
                    <div className="border rounded-lg p-6" style={{ backgroundColor: visualStyle.colors.background }}>
                      {/* Header Section */}
                      <div className="text-center mb-6">
                        <h1 
                          className="text-3xl font-bold mb-2" 
                          style={{ color: visualStyle.colors.primary, fontFamily: visualStyle.font }}
                        >
                          RSVP for Our Wedding
                        </h1>
                        <p className="text-lg mb-2" style={{ color: visualStyle.colors.text, fontFamily: visualStyle.font }}>
                          {form.brideName} & {form.groomName}
                        </p>
                        {form.welcomeMessage && (
                          <p className="text-sm mb-4" style={{ color: visualStyle.colors.text, fontFamily: visualStyle.font }}>
                            {form.welcomeMessage}
                          </p>
                        )}
                      </div>

                      {/* Form Preview */}
                      <div className="max-w-md mx-auto">
                        <div className="space-y-4">
                          {/* Name Fields */}
                          <div className="grid grid-cols-2 gap-3">
                            <div>
                              <label className="block text-sm font-medium mb-1" style={{ color: visualStyle.colors.text, fontFamily: visualStyle.font }}>
                                First Name *
                              </label>
                              <input 
                                type="text" 
                                className="w-full px-3 py-2 border rounded-md" 
                                style={{ 
                                  backgroundColor: '#ffffff',
                                  borderColor: visualStyle.colors.primary,
                                  color: visualStyle.colors.text,
                                  fontFamily: visualStyle.font
                                }}
                                placeholder="Enter first name"
                                disabled
                              />
                            </div>
                            <div>
                              <label className="block text-sm font-medium mb-1" style={{ color: visualStyle.colors.text, fontFamily: visualStyle.font }}>
                                Last Name *
                              </label>
                              <input 
                                type="text" 
                                className="w-full px-3 py-2 border rounded-md" 
                                style={{ 
                                  backgroundColor: '#ffffff',
                                  borderColor: visualStyle.colors.primary,
                                  color: visualStyle.colors.text,
                                  fontFamily: visualStyle.font
                                }}
                                placeholder="Enter last name"
                                disabled
                              />
                            </div>
                          </div>

                          {/* Attending Radio */}
                          <div>
                            <label className="block text-sm font-medium mb-2" style={{ color: visualStyle.colors.text, fontFamily: visualStyle.font }}>
                              Will you be attending? *
                            </label>
                            <div className="space-y-2">
                              <label className="flex items-center">
                                <input type="radio" name="preview-attending" className="mr-2" disabled />
                                <span style={{ color: visualStyle.colors.text, fontFamily: visualStyle.font }}>
                                  Yes, I'll be there
                                </span>
                              </label>
                              <label className="flex items-center">
                                <input type="radio" name="preview-attending" className="mr-2" disabled />
                                <span style={{ color: visualStyle.colors.text, fontFamily: visualStyle.font }}>
                                  Sorry, I can't make it
                                </span>
                              </label>
                            </div>
                          </div>

                          {/* Connection Type */}
                          <div>
                            <label className="block text-sm font-medium mb-1" style={{ color: visualStyle.colors.text, fontFamily: visualStyle.font }}>
                              Connection to the couple *
                            </label>
                            <select 
                              className="w-full px-3 py-2 border rounded-md" 
                              style={{ 
                                backgroundColor: '#ffffff',
                                borderColor: visualStyle.colors.primary,
                                color: visualStyle.colors.text,
                                fontFamily: visualStyle.font
                              }}
                              disabled
                            >
                              <option>Select connection</option>
                              {form.connectionOptions.slice(0, 3).map(option => (
                                <option key={option}>{option}</option>
                              ))}
                            </select>
                          </div>

                          {/* Submit Button */}
                          <button 
                            className="w-full px-6 py-3 rounded-lg font-medium text-white mt-4" 
                            style={{ 
                              backgroundColor: visualStyle.colors.primary,
                              fontFamily: visualStyle.font
                            }}
                            disabled
                          >
                            Submit RSVP
                          </button>
                        </div>
                      </div>

                      {/* Color Legend */}
                      <div className="mt-6 pt-4 border-t border-gray-200">
                        <h4 className="text-sm font-medium mb-2" style={{ color: visualStyle.colors.text, fontFamily: visualStyle.font }}>
                          Color Usage:
                        </h4>
                        <div className="grid grid-cols-3 gap-4 text-xs">
                          <div className="flex items-center">
                            <div className="w-4 h-4 rounded mr-2" style={{ backgroundColor: visualStyle.colors.primary }}></div>
                            <span style={{ color: visualStyle.colors.text, fontFamily: visualStyle.font }}>Primary (buttons, accents)</span>
                          </div>
                          <div className="flex items-center">
                            <div className="w-4 h-4 rounded mr-2" style={{ backgroundColor: visualStyle.colors.background }}></div>
                            <span style={{ color: visualStyle.colors.text, fontFamily: visualStyle.font }}>Background</span>
                          </div>
                          <div className="flex items-center">
                            <div className="w-4 h-4 rounded mr-2" style={{ backgroundColor: visualStyle.colors.text }}></div>
                            <span style={{ color: visualStyle.colors.text, fontFamily: visualStyle.font }}>Text</span>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              )}
            </div>
          )}
        </div>
        {/* Action Buttons */}
        <div className="flex justify-between items-center">
          <button 
            className="btn-secondary" 
            onClick={() => {
              if (currentWedding?.weddingId) {
                window.open(`http://localhost:3000/rsvp/${currentWedding.weddingId}`, '_blank');
              }
            }}
            disabled={!currentWedding?.weddingId}
          >
            Preview RSVP Form
          </button>
          <button className="btn-primary" onClick={handleSave} disabled={saving}>
            {saving ? 'Saving...' : 'Save Changes'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default CustomizeWeddingPage;
