import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { 
  Plus, 
  Settings, 
  Users, 
  ExternalLink, 
  Trash2, 
  LogOut,
  Heart,
  Calendar,
  MapPin,
  Copy,
  Edit
} from 'lucide-react';
import axios from 'axios';
import toast from 'react-hot-toast';
import { useNavigate } from 'react-router-dom';
import CustomizeRSVPForm from './CustomizeRSVPForm';

const WeddingManager = () => {
  const { logout } = useAuth();
  const [weddings, setWeddings] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const { setCurrentWedding } = useAuth();

  useEffect(() => {
    fetchWeddings();
  }, []);

  const fetchWeddings = async () => {
    try {
      const response = await axios.get('/api/weddings');
      setWeddings(response.data.weddings);
    } catch (error) {
      toast.error('Failed to fetch weddings');
    } finally {
      setLoading(false);
    }
  };

  const handleCreateWeddingClick = async () => {
    try {
      // Create a default wedding with current date + 1 year
      const defaultDate = new Date();
      defaultDate.setFullYear(defaultDate.getFullYear() + 1);

      const weddingData = {
        brideName: 'Bride',
        groomName: 'Groom',
        weddingDate: defaultDate.toISOString().split('T')[0],
        ceremonyTime: '18:00',
        venueDetails: '',
        welcomeMessage: 'We are excited to celebrate our special day with you!',
        language: 'en',
        colors: {},
        fonts: {},
        layoutStyle: 'modern',
        countdownStyle: 'digital',
        connectionOptions: [
          'Friend of the Bride',
          'Friend of the Groom',
          'Family of the Bride',
          'Family of the Groom'
        ]
      };

      const response = await axios.post('/api/weddings', weddingData);
      const newWedding = response.data.wedding;

      // Set as current wedding and navigate to dashboard
      setCurrentWedding(newWedding);
      localStorage.setItem('currentWedding', JSON.stringify(newWedding));
      navigate('/dashboard');

      toast.success('Wedding created successfully!');
    } catch (error) {
      toast.error('Failed to create wedding');
    }
  };

  const handleDeleteWedding = async (weddingId) => {
    if (!window.confirm('Are you sure you want to delete this wedding? This action cannot be undone.')) {
      return;
    }

    try {
      await axios.delete(`/api/weddings/${weddingId}`);
      setWeddings(weddings.filter(w => w.weddingId !== weddingId));
      toast.success('Wedding deleted successfully');
    } catch (error) {
      toast.error('Failed to delete wedding');
    }
  };

  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text);
    toast.success('Copied to clipboard!');
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const handleWeddingClick = (wedding) => {
    setCurrentWedding(wedding);
    localStorage.setItem('currentWedding', JSON.stringify(wedding));
    navigate('/dashboard');
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-pink-50 via-purple-50 to-blue-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-16 w-16 border-4 border-pink-200 border-t-purple-600 mx-auto mb-4"></div>
          <p className="text-lg text-gray-700 font-medium">Loading your beautiful weddings...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-pink-50 via-purple-50 to-blue-50 p-6">
      {/* Header */}
      <div className="max-w-7xl mx-auto">
        <div className="flex justify-between items-center mb-8">
          <div>
            <h1 className="text-5xl font-bold bg-gradient-to-r from-pink-600 via-purple-600 to-blue-600 bg-clip-text text-transparent mb-3">
              Wedding Manager
            </h1>
            <p className="text-lg text-gray-700 font-medium">
              âœ¨ Manage all your beautiful weddings and RSVP forms
            </p>
          </div>
          <div className="flex space-x-4">
            <button
              onClick={handleCreateWeddingClick}
              className="bg-gradient-to-r from-pink-500 to-purple-600 hover:from-pink-600 hover:to-purple-700 text-white px-6 py-3 rounded-xl font-semibold flex items-center shadow-lg hover:shadow-xl transform hover:scale-105 transition-all duration-200"
            >
              <Plus className="h-5 w-5 mr-2" />
              Create Wedding
            </button>
            <button
              onClick={logout}
              className="bg-white hover:bg-gray-50 text-gray-700 px-6 py-3 rounded-xl font-semibold flex items-center shadow-lg hover:shadow-xl border-2 border-gray-200 hover:border-gray-300 transform hover:scale-105 transition-all duration-200"
            >
              <LogOut className="h-5 w-5 mr-2" />
              Logout
            </button>
          </div>
        </div>

        {/* Weddings Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {weddings.map((wedding, index) => (
            <div
              key={wedding.weddingId}
              className="bg-white rounded-2xl shadow-xl hover:shadow-2xl transform hover:scale-105 transition-all duration-300 cursor-pointer border border-gray-100 overflow-hidden group"
              onClick={() => handleWeddingClick(wedding)}
            >
              {/* Gradient Header */}
              <div className="bg-gradient-to-r from-pink-500 to-purple-600 p-6 text-white">
                <div className="flex justify-between items-start">
                  <div className="flex-1">
                    <h3 className="text-2xl font-bold mb-2">
                      {wedding.brideName} & {wedding.groomName}
                    </h3>
                    <div className="flex items-center text-pink-100 mb-2">
                      <Calendar className="h-5 w-5 mr-2" />
                      {formatDate(wedding.weddingDate)}
                    </div>
                    {wedding.venueDetails && (
                      <div className="flex items-center text-pink-100">
                        <MapPin className="h-5 w-5 mr-2" />
                        {wedding.venueDetails}
                      </div>
                    )}
                  </div>
                  <button
                    onClick={e => { e.stopPropagation(); handleDeleteWedding(wedding.weddingId); }}
                    className="p-2 text-pink-100 hover:text-red-300 transition-colors rounded-lg hover:bg-white hover:bg-opacity-20"
                    title="Delete Wedding"
                  >
                    <Trash2 className="h-5 w-5" />
                  </button>
                </div>
              </div>

              {/* Content */}
              <div className="p-6 space-y-4">
                {/* Admin Code */}
                <div className="bg-gradient-to-r from-purple-50 to-pink-50 rounded-xl p-4 border border-purple-100">
                  <div className="flex justify-between items-center mb-2">
                    <span className="text-sm font-semibold text-purple-700">Admin Code:</span>
                    <button
                      onClick={e => { e.stopPropagation(); copyToClipboard(wedding.adminCode); }}
                      className="text-purple-600 hover:text-purple-800 p-1 rounded-lg hover:bg-purple-100 transition-colors"
                    >
                      <Copy className="h-4 w-4" />
                    </button>
                  </div>
                  <code className="text-sm bg-white px-3 py-2 rounded-lg border border-purple-200 block font-mono text-purple-800">
                    {wedding.adminCode}
                  </code>
                </div>

                {/* RSVP Link */}
                <div className="bg-gradient-to-r from-blue-50 to-cyan-50 rounded-xl p-4 border border-blue-100">
                  <div className="flex justify-between items-center mb-2">
                    <span className="text-sm font-semibold text-blue-700">RSVP Link:</span>
                    <a
                      href={`/rsvp/${wedding.weddingId}`}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="text-blue-600 hover:text-blue-800 p-1 rounded-lg hover:bg-blue-100 transition-colors"
                    >
                      <ExternalLink className="h-4 w-4" />
                    </a>
                  </div>
                  <code className="text-xs bg-white px-3 py-2 rounded-lg border border-blue-200 block break-all font-mono text-blue-800">
                    {window.location.origin}/rsvp/{wedding.weddingId}
                  </code>
                </div>

                {/* Quick Stats */}
                <div className="flex justify-between items-center bg-gradient-to-r from-green-50 to-emerald-50 rounded-xl p-4 border border-green-100">
                  <div className="flex items-center">
                    <Users className="h-4 w-4 text-green-600 mr-2" />
                    <span className="text-sm font-semibold text-green-700">RSVPs: {wedding.rsvpCount || 0}</span>
                  </div>
                  <div className="flex items-center">
                    <span className="text-sm font-semibold text-emerald-700">
                      {wedding.language === 'he' ? 'Hebrew' : 'English'}
                    </span>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Empty State */}
        {weddings.length === 0 && (
          <div className="text-center py-16">
            <div className="bg-gradient-to-r from-pink-500 to-purple-600 rounded-full p-6 w-24 h-24 mx-auto mb-6 flex items-center justify-center">
              <Heart className="h-12 w-12 text-white" />
            </div>
            <h3 className="text-2xl font-bold text-gray-800 mb-3">
              No weddings yet
            </h3>
            <p className="text-gray-600 mb-8 text-lg">
              Create your first beautiful wedding to get started
            </p>
            <button
              onClick={handleCreateWeddingClick}
              className="bg-gradient-to-r from-pink-500 to-purple-600 hover:from-pink-600 hover:to-purple-700 text-white px-8 py-4 rounded-xl font-semibold text-lg shadow-lg hover:shadow-xl transform hover:scale-105 transition-all duration-200"
            >
              <Plus className="h-6 w-6 mr-2 inline" />
              Create Your First Wedding
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default WeddingManager;
