import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { 
  Plus, 
  Settings, 
  Users, 
  ExternalLink, 
  Trash2, 
  LogOut,
  Heart,
  Calendar,
  MapPin,
  Copy
} from 'lucide-react';
import axios from 'axios';
import toast from 'react-hot-toast';
import CreateWeddingModal from './CreateWeddingModal';

const WeddingManager = () => {
  const { logout } = useAuth();
  const [weddings, setWeddings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [selectedWedding, setSelectedWedding] = useState(null);

  useEffect(() => {
    fetchWeddings();
  }, []);

  const fetchWeddings = async () => {
    try {
      const response = await axios.get('/api/weddings');
      setWeddings(response.data.weddings);
    } catch (error) {
      toast.error('Failed to fetch weddings');
    } finally {
      setLoading(false);
    }
  };

  const handleCreateWedding = async (weddingData) => {
    try {
      const response = await axios.post('/api/weddings', weddingData);
      setWeddings([response.data.wedding, ...weddings]);
      setShowCreateModal(false);
      toast.success('Wedding created successfully!');
    } catch (error) {
      toast.error('Failed to create wedding');
    }
  };

  const handleDeleteWedding = async (weddingId) => {
    if (!window.confirm('Are you sure you want to delete this wedding? This action cannot be undone.')) {
      return;
    }

    try {
      await axios.delete(`/api/weddings/${weddingId}`);
      setWeddings(weddings.filter(w => w.weddingId !== weddingId));
      toast.success('Wedding deleted successfully');
    } catch (error) {
      toast.error('Failed to delete wedding');
    }
  };

  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text);
    toast.success('Copied to clipboard!');
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-purple-600"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen p-6">
      {/* Header */}
      <div className="max-w-7xl mx-auto">
        <div className="flex justify-between items-center mb-8">
          <div>
            <h1 className="text-4xl font-bold text-gradient mb-2">
              Wedding Manager
            </h1>
            <p className="text-gray-600">
              Manage all your weddings and RSVP forms
            </p>
          </div>
          <div className="flex space-x-4">
            <button
              onClick={() => setShowCreateModal(true)}
              className="btn-primary flex items-center"
            >
              <Plus className="h-5 w-5 mr-2" />
              Create Wedding
            </button>
            <button
              onClick={logout}
              className="btn-secondary flex items-center"
            >
              <LogOut className="h-5 w-5 mr-2" />
              Logout
            </button>
          </div>
        </div>

        {/* Stats */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
          <div className="stat-card">
            <div className="stat-number">{weddings.length}</div>
            <div className="stat-label">Total Weddings</div>
          </div>
          <div className="stat-card">
            <div className="stat-number">
              {weddings.reduce((sum, w) => sum + (w.rsvpCount || 0), 0)}
            </div>
            <div className="stat-label">Total RSVPs</div>
          </div>
          <div className="stat-card">
            <div className="stat-number">
              {weddings.filter(w => new Date(w.weddingDate) > new Date()).length}
            </div>
            <div className="stat-label">Upcoming</div>
          </div>
          <div className="stat-card">
            <div className="stat-number">
              {weddings.filter(w => new Date(w.weddingDate) <= new Date()).length}
            </div>
            <div className="stat-label">Completed</div>
          </div>
        </div>

        {/* Weddings Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {weddings.map((wedding) => (
            <div key={wedding.weddingId} className="dashboard-card group">
              {/* Wedding Header */}
              <div className="flex justify-between items-start mb-4">
                <div className="flex-1">
                  <h3 className="text-xl font-semibold text-gray-900 mb-1">
                    {wedding.brideName} & {wedding.groomName}
                  </h3>
                  <div className="flex items-center text-sm text-gray-600 mb-2">
                    <Calendar className="h-4 w-4 mr-1" />
                    {formatDate(wedding.weddingDate)}
                  </div>
                  {wedding.venueDetails && (
                    <div className="flex items-center text-sm text-gray-600">
                      <MapPin className="h-4 w-4 mr-1" />
                      {wedding.venueDetails}
                    </div>
                  )}
                </div>
                <div className="flex space-x-2">
                  <button
                    onClick={() => setSelectedWedding(wedding)}
                    className="p-2 text-gray-400 hover:text-purple-600 transition-colors"
                    title="View Details"
                  >
                    <Settings className="h-5 w-5" />
                  </button>
                  <button
                    onClick={() => handleDeleteWedding(wedding.weddingId)}
                    className="p-2 text-gray-400 hover:text-red-600 transition-colors"
                    title="Delete Wedding"
                  >
                    <Trash2 className="h-5 w-5" />
                  </button>
                </div>
              </div>

              {/* Admin Code */}
              <div className="bg-gray-50 rounded-lg p-3 mb-4">
                <div className="flex justify-between items-center">
                  <span className="text-sm font-medium text-gray-700">Admin Code:</span>
                  <button
                    onClick={() => copyToClipboard(wedding.adminCode)}
                    className="text-purple-600 hover:text-purple-700"
                  >
                    <Copy className="h-4 w-4" />
                  </button>
                </div>
                <code className="text-sm bg-white px-2 py-1 rounded border mt-1 block">
                  {wedding.adminCode}
                </code>
              </div>

              {/* RSVP Link */}
              <div className="bg-gray-50 rounded-lg p-3 mb-4">
                <div className="flex justify-between items-center mb-2">
                  <span className="text-sm font-medium text-gray-700">RSVP Link:</span>
                  <a
                    href={`/rsvp/${wedding.weddingId}`}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-purple-600 hover:text-purple-700"
                  >
                    <ExternalLink className="h-4 w-4" />
                  </a>
                </div>
                <code className="text-xs bg-white px-2 py-1 rounded border block break-all">
                  {window.location.origin}/rsvp/{wedding.weddingId}
                </code>
              </div>

              {/* Quick Stats */}
              <div className="flex justify-between text-sm text-gray-600">
                <span>RSVPs: {wedding.rsvpCount || 0}</span>
                <span>Language: {wedding.language === 'he' ? 'Hebrew' : 'English'}</span>
              </div>
            </div>
          ))}
        </div>

        {/* Empty State */}
        {weddings.length === 0 && (
          <div className="text-center py-12">
            <Heart className="h-16 w-16 text-gray-300 mx-auto mb-4" />
            <h3 className="text-xl font-semibold text-gray-900 mb-2">
              No weddings yet
            </h3>
            <p className="text-gray-600 mb-6">
              Create your first wedding to get started
            </p>
            <button
              onClick={() => setShowCreateModal(true)}
              className="btn-primary"
            >
              <Plus className="h-5 w-5 mr-2" />
              Create Your First Wedding
            </button>
          </div>
        )}
      </div>

      {/* Create Wedding Modal */}
      {showCreateModal && (
        <CreateWeddingModal
          onClose={() => setShowCreateModal(false)}
          onSubmit={handleCreateWedding}
        />
      )}
    </div>
  );
};

export default WeddingManager;
