import React from 'react';

const CustomizeWeddingPage = () => {
  return (
    <div className="min-h-screen p-8 bg-gray-50">
      <div className="max-w-3xl mx-auto bg-white rounded-lg shadow-lg p-8">
        <h1 className="text-3xl font-bold mb-8 text-center">Customize Your RSVP Form</h1>
        {/* 1. RSVP Form Settings */}
        <section className="mb-8">
          <h2 className="text-xl font-semibold mb-4">RSVP Form Settings</h2>
          {/* TODO: Bride’s name, Groom’s name, Welcome sentence, Countdown timer, Primary language, Layout preview */}
          <div className="text-gray-500">Form settings go here...</div>
        </section>
        {/* 2. Connection Options */}
        <section className="mb-8">
          <h2 className="text-xl font-semibold mb-4">Connection Options</h2>
          {/* TODO: Only show checked options, allow reorder/rename, hide unchecked */}
          <div className="text-gray-500">Connection options go here...</div>
        </section>
        {/* 3. Visual Style Customization */}
        <section className="mb-8">
          <h2 className="text-xl font-semibold mb-4">Visual Style</h2>
          {/* TODO: Color pickers, font selection, live preview */}
          <div className="text-gray-500">Visual style customization goes here...</div>
        </section>
        {/* Save Button */}
        <div className="flex justify-end">
          <button className="btn-primary">Save Changes</button>
        </div>
      </div>
    </div>
  );
};

export default CustomizeWeddingPage;
