import React, { useState } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';
import axios from 'axios'; // Added for saving changes
import { toast } from 'react-toastify'; // Added for toast notifications

const defaultConnectionOptions = [
  'Friend of the Bride',
  'Friend of the Groom',
  'Family of the Bride',
  'Family of the Groom',
  'College Friend',
  'Work Colleague',
  'Neighbor',
  'Other'
];

const CustomizeWeddingPage = () => {
  const { currentWedding } = useAuth();
  const navigate = useNavigate();
  const [form, setForm] = useState({
    brideName: currentWedding?.brideName || '',
    groomName: currentWedding?.groomName || '',
    welcomeMessage: currentWedding?.welcomeMessage || '',
    weddingDate: currentWedding?.weddingDate || '',
    ceremonyTime: currentWedding?.ceremonyTime || '',
    language: currentWedding?.language || 'en',
    connectionOptions: currentWedding?.connectionOptions?.length > 0 ? currentWedding.connectionOptions : [
      'Friend of the Bride',
      'Friend of the Groom',
      'Family of the Bride',
      'Family of the Groom'
    ],
  });
  const [activeTab, setActiveTab] = useState('form');
  const [newConnection, setNewConnection] = useState('');

  // Add state for visual style customization
  const [visualStyle, setVisualStyle] = useState({
    colors: {
      primary: currentWedding?.colors?.primary || '#8B5CF6',
      background: currentWedding?.colors?.background || '#FFFFFF',
      text: currentWedding?.colors?.text || '#1F2937'
    },
    font: currentWedding?.fonts?.main || 'Inter',
    countdownStyle: currentWedding?.countdownStyle || 'digital'
  });

  const [saving, setSaving] = useState(false);

  const handleChange = (field, value) => {
    setForm(prev => ({ ...prev, [field]: value }));
  };

  const handleRemoveConnection = (option) => {
    setForm(prev => ({
      ...prev,
      connectionOptions: prev.connectionOptions.filter(o => o !== option)
    }));
  };

  const handleAddConnection = () => {
    if (newConnection.trim() && !form.connectionOptions.includes(newConnection.trim())) {
      setForm(prev => ({
        ...prev,
        connectionOptions: [...prev.connectionOptions, newConnection.trim()]
      }));
      setNewConnection('');
    }
  };

  const handleVisualChange = (field, value) => {
    setVisualStyle(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleColorChange = (colorType, value) => {
    setVisualStyle(prev => ({
      ...prev,
      colors: {
        ...prev.colors,
        [colorType]: value
      }
    }));
  };

  const handleSave = async () => {
    setSaving(true);
    try {
      const updatedData = {
        ...form,
        colors: visualStyle.colors,
        fonts: { main: visualStyle.font },
        countdownStyle: visualStyle.countdownStyle
      };
      await axios.put(`/api/weddings/${currentWedding.weddingId}`, updatedData);
      toast.success('Changes saved successfully!');
    } catch (error) {
      toast.error('Failed to save changes');
    } finally {
      setSaving(false);
    }
  };

  return (
    <div className="min-h-screen p-8 bg-gray-50">
      <div className="max-w-3xl mx-auto bg-white rounded-lg shadow-lg p-8">
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-3xl font-bold text-center flex-1">Customize Your RSVP Form</h1>
          <button className="btn-secondary ml-4" onClick={() => navigate('/dashboard')}>
            Back to Dashboard
          </button>
        </div>
        {/* Single Accordion/Dropdown for RSVP Customization */}
        <div className="mb-8">
          <button className="w-full text-left py-3 px-4 bg-gray-100 rounded-t-lg font-semibold" onClick={() => setActiveTab(activeTab ? '' : 'form')}>
            RSVP Customization
          </button>
          {activeTab && (
            <div className="border border-t-0 rounded-b-lg p-4">
              {/* Tabs/Buttons for navigation */}
              <div className="flex space-x-4 mb-6">
                <button className={`px-4 py-2 rounded ${activeTab === 'form' ? 'bg-purple-100 text-purple-700 font-bold' : 'bg-gray-100 text-gray-700'}`} onClick={() => setActiveTab('form')}>Form Settings</button>
                <button className={`px-4 py-2 rounded ${activeTab === 'connections' ? 'bg-purple-100 text-purple-700 font-bold' : 'bg-gray-100 text-gray-700'}`} onClick={() => setActiveTab('connections')}>Connection Options</button>
                <button className={`px-4 py-2 rounded ${activeTab === 'style' ? 'bg-purple-100 text-purple-700 font-bold' : 'bg-gray-100 text-gray-700'}`} onClick={() => setActiveTab('style')}>Visual Style</button>
              </div>
              {/* Tab Content */}
              {activeTab === 'form' && (
                <div className="space-y-4">
                  <div>
                    <label className="form-label">Bride's Name</label>
                    <input
                      type="text"
                      className="form-input"
                      value={form.brideName}
                      onChange={e => handleChange('brideName', e.target.value)}
                      placeholder="Enter bride's name"
                    />
                  </div>
                  <div>
                    <label className="form-label">Groom's Name</label>
                    <input
                      type="text"
                      className="form-input"
                      value={form.groomName}
                      onChange={e => handleChange('groomName', e.target.value)}
                      placeholder="Enter groom's name"
                    />
                  </div>
                  <div>
                    <label className="form-label">Welcome Sentence</label>
                    <textarea
                      className="form-input"
                      value={form.welcomeMessage}
                      onChange={e => handleChange('welcomeMessage', e.target.value)}
                      placeholder="Enter a welcome message"
                    />
                  </div>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                      <label className="form-label">Wedding Date</label>
                      <input
                        type="date"
                        className="form-input"
                        value={form.weddingDate}
                        onChange={e => handleChange('weddingDate', e.target.value)}
                      />
                    </div>
                    <div>
                      <label className="form-label">Ceremony Time</label>
                      <input
                        type="time"
                        className="form-input"
                        value={form.ceremonyTime}
                        onChange={e => handleChange('ceremonyTime', e.target.value)}
                      />
                    </div>
                  </div>
                  <div>
                    <label className="form-label">Primary Language</label>
                    <select
                      className="form-select"
                      value={form.language}
                      onChange={e => handleChange('language', e.target.value)}
                    >
                      <option value="en">English ðŸ‡ºðŸ‡¸</option>
                      <option value="he">Hebrew ðŸ‡®ðŸ‡±</option>
                    </select>
                  </div>
                </div>
              )}
              {activeTab === 'connections' && (
                <div>
                  <div className="mb-4">
                    <label className="form-label">Connection Options</label>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                      {form.connectionOptions.map(option => (
                        <div key={option} className="flex items-center justify-between">
                          <span className="text-sm text-gray-700">{option}</span>
                          <button type="button" className="text-red-500 ml-2" onClick={() => handleRemoveConnection(option)}>Remove</button>
                        </div>
                      ))}
                    </div>
                  </div>
                  <div className="flex space-x-2 mt-4">
                    <input
                      type="text"
                      className="form-input flex-1"
                      value={newConnection}
                      onChange={e => setNewConnection(e.target.value)}
                      placeholder="Add new connection option"
                    />
                    <button type="button" className="btn-secondary" onClick={handleAddConnection}>Add</button>
                  </div>
                </div>
              )}
              {activeTab === 'style' && (
                <div className="space-y-6">
                  {/* Theme Colors */}
                  <div>
                    <h3 className="text-lg font-semibold mb-4">Theme Colors</h3>
                    <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                      <div>
                        <label className="form-label">Primary Color</label>
                        <input
                          type="color"
                          className="w-full h-10 rounded border"
                          value={visualStyle.colors.primary}
                          onChange={e => handleColorChange('primary', e.target.value)}
                        />
                      </div>
                      <div>
                        <label className="form-label">Background Color</label>
                        <input
                          type="color"
                          className="w-full h-10 rounded border"
                          value={visualStyle.colors.background}
                          onChange={e => handleColorChange('background', e.target.value)}
                        />
                      </div>
                      <div>
                        <label className="form-label">Text Color</label>
                        <input
                          type="color"
                          className="w-full h-10 rounded border"
                          value={visualStyle.colors.text}
                          onChange={e => handleColorChange('text', e.target.value)}
                        />
                      </div>
                    </div>
                  </div>
                  {/* Font Options */}
                  <div>
                    <h3 className="text-lg font-semibold mb-4">Font Options</h3>
                    <select
                      className="form-select"
                      value={visualStyle.font}
                      onChange={e => handleVisualChange('font', e.target.value)}
                    >
                      <option value="Inter">Inter - Modern & Clean</option>
                      <option value="Playfair Display">Playfair Display - Elegant Serif</option>
                      <option value="Roboto">Roboto - Professional</option>
                      <option value="Dancing Script">Dancing Script - Handwritten</option>
                      <option value="Montserrat">Montserrat - Contemporary</option>
                    </select>
                    <div className="mt-2 p-3 bg-gray-50 rounded" style={{ fontFamily: visualStyle.font }}>
                      <p className="text-sm">Preview: This is how your text will look with {visualStyle.font}</p>
                    </div>
                  </div>
                  {/* Countdown Clock Styles */}
                  <div>
                    <h3 className="text-lg font-semibold mb-4">Countdown Clock Style</h3>
                    <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                      <label className="flex items-center p-3 border rounded cursor-pointer">
                        <input
                          type="radio"
                          name="countdownStyle"
                          value="digital"
                          checked={visualStyle.countdownStyle === 'digital'}
                          onChange={e => handleVisualChange('countdownStyle', e.target.value)}
                          className="mr-2"
                        />
                        <div>
                          <div className="font-semibold">Digital</div>
                          <div className="text-xs text-gray-500">Modern digital display</div>
                        </div>
                      </label>
                      <label className="flex items-center p-3 border rounded cursor-pointer">
                        <input
                          type="radio"
                          name="countdownStyle"
                          value="circles"
                          checked={visualStyle.countdownStyle === 'circles'}
                          onChange={e => handleVisualChange('countdownStyle', e.target.value)}
                          className="mr-2"
                        />
                        <div>
                          <div className="font-semibold">Circles</div>
                          <div className="text-xs text-gray-500">Elegant circular design</div>
                        </div>
                      </label>
                      <label className="flex items-center p-3 border rounded cursor-pointer">
                        <input
                          type="radio"
                          name="countdownStyle"
                          value="elegant"
                          checked={visualStyle.countdownStyle === 'elegant'}
                          onChange={e => handleVisualChange('countdownStyle', e.target.value)}
                          className="mr-2"
                        />
                        <div>
                          <div className="font-semibold">Elegant Lines</div>
                          <div className="text-xs text-gray-500">Sophisticated line design</div>
                        </div>
                      </label>
                    </div>
                  </div>
                  {/* Live Preview */}
                  <div>
                    <h3 className="text-lg font-semibold mb-4">Live Preview</h3>
                    <div className="border rounded-lg p-4" style={{ backgroundColor: visualStyle.colors.background }}>
                      <div className="text-center mb-4">
                        <h2 className="text-2xl font-bold mb-2" style={{ color: visualStyle.colors.text, fontFamily: visualStyle.font }}>
                          {form.brideName} & {form.groomName}
                        </h2>
                        <p className="text-sm" style={{ color: visualStyle.colors.text, fontFamily: visualStyle.font }}>
                          {form.welcomeMessage || 'Welcome message preview'}
                        </p>
                      </div>
                      <div className="text-center">
                        <button className="px-6 py-2 rounded-lg text-white font-medium" style={{ backgroundColor: visualStyle.colors.primary }}>
                          RSVP Button Preview
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              )}
            </div>
          )}
        </div>
        {/* Save Button */}
        <div className="flex justify-end">
          <button className="btn-primary" onClick={handleSave} disabled={saving}>
            {saving ? 'Saving...' : 'Save Changes'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default CustomizeWeddingPage;
