import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { 
  Users, 
  Calendar, 
  MapPin, 
  Settings, 
  Download, 
  LogOut,
  Heart,
  TrendingUp,
  UserCheck,
  UserX,
  BarChart3,
  Edit
} from 'lucide-react';
import axios from 'axios';
import toast from 'react-hot-toast';
import { Menu } from '@headlessui/react';

const WeddingDashboard = () => {
  const { currentWedding, logoutFromWedding } = useAuth();
  const [stats, setStats] = useState(null);
  const [responses, setResponses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('overview');
  const [filter, setFilter] = useState('all');
  const [showCustomizeConnections, setShowCustomizeConnections] = useState(false);

  useEffect(() => {
    if (currentWedding) {
      fetchData();
    }
  }, [currentWedding]);

  const fetchData = async () => {
    try {
      const [statsResponse, responsesResponse] = await Promise.all([
        axios.get(`/api/rsvp/${currentWedding.weddingId}/statistics`),
        axios.get(`/api/rsvp/${currentWedding.weddingId}/responses`)
      ]);

      setStats(statsResponse.data.statistics);
      setResponses(responsesResponse.data.responses);
    } catch (error) {
      toast.error('Failed to fetch wedding data');
    } finally {
      setLoading(false);
    }
  };

  const handleExport = async (filterType = 'all') => {
    try {
      const response = await axios.get(`/api/rsvp/${currentWedding.weddingId}/export?filter=${filterType}`, {
        responseType: 'blob'
      });

      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `guest-list-${currentWedding.weddingId}-${filterType}.csv`);
      document.body.appendChild(link);
      link.click();
      link.remove();

      toast.success('Guest list exported successfully!');
    } catch (error) {
      toast.error('Failed to export guest list');
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-purple-600"></div>
      </div>
    );
  }

  const filteredResponses = responses.filter(response => {
    if (filter === 'not-attending') return !response.attending;
    if (!response.attending) return false;
    if (filter === 'all') return response.attending;
    if (filter === 'bride-friend') return response.connectionType === 'Friend of the Bride';
    if (filter === 'groom-friend') return response.connectionType === 'Friend of the Groom';
    if (filter === 'bride-family') return response.connectionType === 'Family of the Bride';
    if (filter === 'groom-family') return response.connectionType === 'Family of the Groom';
    return true;
  });

  const nameCounts = responses.reduce((acc, r) => {
    const key = `${r.firstName?.trim().toLowerCase()} ${r.lastName?.trim().toLowerCase()}`;
    acc[key] = (acc[key] || 0) + 1;
    return acc;
  }, {});

  return (
    <div className="min-h-screen p-6">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="flex justify-between items-center mb-8">
          <div>
            <h1 className="text-4xl font-bold text-gradient mb-2">
              Wedding Dashboard
            </h1>
            <p className="text-gray-600">
              {currentWedding.brideName} & {currentWedding.groomName}
            </p>
          </div>
          <div className="relative">
            <Menu as="div" className="relative inline-block text-left">
              <Menu.Button className="btn-secondary flex items-center">
                <Settings className="h-5 w-5 mr-2" />
                Menu
              </Menu.Button>
              <Menu.Items className="absolute right-0 mt-2 w-56 origin-top-right bg-white border border-gray-200 divide-y divide-gray-100 rounded-md shadow-lg focus:outline-none z-50">
                <div className="py-1">
                  <Menu.Item>
                    {({ active }) => (
                      <button
                        onClick={() => setActiveTab('overview')}
                        className={`${active ? 'bg-gray-100' : ''} w-full text-left px-4 py-2 text-sm text-gray-700`}
                      >
                        <BarChart3 className="h-4 w-4 inline mr-2" /> Overview
                      </button>
                    )}
                  </Menu.Item>
                  <Menu.Item>
                    {({ active }) => (
                      <button
                        onClick={() => setActiveTab('guest-list')}
                        className={`${active ? 'bg-gray-100' : ''} w-full text-left px-4 py-2 text-sm text-gray-700`}
                      >
                        <Users className="h-4 w-4 inline mr-2" /> Guest List
                      </button>
                    )}
                  </Menu.Item>
                  <Menu.Item>
                    {({ active }) => (
                      <button
                        onClick={() => setShowCustomizeConnections(true)}
                        className={`${active ? 'bg-gray-100' : ''} w-full text-left px-4 py-2 text-sm text-gray-700`}
                      >
                        <Edit className="h-4 w-4 inline mr-2" /> Customize Connections
                      </button>
                    )}
                  </Menu.Item>
                  <Menu.Item>
                    {({ active }) => (
                      <button
                        onClick={logoutFromWedding}
                        className={`${active ? 'bg-gray-100' : ''} w-full text-left px-4 py-2 text-sm text-gray-700`}
                      >
                        <LogOut className="h-4 w-4 inline mr-2" /> Logout
                      </button>
                    )}
                  </Menu.Item>
                </div>
              </Menu.Items>
            </Menu>
          </div>
        </div>

        {/* Wedding Info */}
        <div className="card mb-8">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <div className="flex items-center">
              <Calendar className="h-6 w-6 text-purple-600 mr-3" />
              <div>
                <p className="font-medium text-gray-900">Wedding Date</p>
                <p className="text-gray-600">{formatDate(currentWedding.weddingDate)}</p>
              </div>
            </div>
            <div className="flex items-center">
              <MapPin className="h-6 w-6 text-purple-600 mr-3" />
              <div>
                <p className="font-medium text-gray-900">Venue</p>
                <p className="text-gray-600">{currentWedding.venueDetails || 'Not specified'}</p>
              </div>
            </div>
            <div className="flex items-center">
              <Users className="h-6 w-6 text-purple-600 mr-3" />
              <div>
                <p className="font-medium text-gray-900">Language</p>
                <p className="text-gray-600">{currentWedding.language === 'he' ? 'Hebrew' : 'English'}</p>
              </div>
            </div>
          </div>
        </div>

        {/* Overview Tab */}
        {activeTab === 'overview' && (
          <div className="space-y-8">
            {/* Stats Cards */}
            <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
              <div className="stat-card">
                <div className="stat-number">{stats?.total || 0}</div>
                <div className="stat-label">Total RSVPs</div>
              </div>
              <div className="stat-card">
                <div className="stat-number">{stats?.attending || 0}</div>
                <div className="stat-label">Attending</div>
              </div>
              <div className="stat-card">
                <div className="stat-number">{stats?.notAttending || 0}</div>
                <div className="stat-label">Not Attending</div>
              </div>
              <div className="stat-card">
                <div className="stat-number">{stats?.guestCount || 0}</div>
                <div className="stat-label">Total Guests</div>
              </div>
            </div>

            {/* Connection Breakdown */}
            {stats?.connectionBreakdown && Object.keys(stats.connectionBreakdown).length > 0 && (
              <div className="card">
                <h3 className="text-xl font-semibold text-gray-900 mb-4">Connection Breakdown</h3>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                  {Object.entries(stats.connectionBreakdown).map(([connection, count]) => (
                    <div key={connection} className="bg-gray-50 rounded-lg p-4">
                      <div className="flex justify-between items-center">
                        <span className="font-medium text-gray-900">{connection}</span>
                        <span className="text-2xl font-bold text-purple-600">{count}</span>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
        )}

        {/* Guest List Tab */}
        {activeTab === 'guest-list' && (
          <div className="space-y-6">
            {/* Filter Controls */}
            <div className="card">
              <div className="flex justify-between items-center">
                <h3 className="text-xl font-semibold text-gray-900">Guest List</h3>
                <div className="flex space-x-2">
                  <select
                    value={filter}
                    onChange={(e) => setFilter(e.target.value)}
                    className="form-select w-auto"
                  >
                    <option value="all">All Attending</option>
                    <option value="bride-friend">Friends of the Bride</option>
                    <option value="groom-friend">Friends of the Groom</option>
                    <option value="bride-family">Family of the Bride</option>
                    <option value="groom-family">Family of the Groom</option>
                    <option value="not-attending">Not Attending</option>
                  </select>
                  <button
                    onClick={() => handleExport(filter)}
                    className="btn-secondary flex items-center"
                  >
                    <Download className="h-5 w-5 mr-2" />
                    Export
                  </button>
                </div>
              </div>
            </div>

            {/* Guest List Table */}
            <div className="card">
              <div className="overflow-x-auto">
                <table className="w-full">
                  <thead>
                    <tr className="border-b border-gray-200">
                      <th className="text-left py-3 px-4 font-semibold text-gray-900">Name</th>
                      <th className="text-left py-3 px-4 font-semibold text-gray-900">Status</th>
                      <th className="text-left py-3 px-4 font-semibold text-gray-900">Connection</th>
                      <th className="text-left py-3 px-4 font-semibold text-gray-900">Guests</th>
                      <th className="text-left py-3 px-4 font-semibold text-gray-900">Submitted</th>
                    </tr>
                  </thead>
                  <tbody>
                    {filteredResponses.map((response) => (
                      <tr key={response.id} className="border-b border-gray-100 hover:bg-gray-50">
                        <td className="py-3 px-4">
                          <div className="font-medium text-gray-900 flex items-center">
                            {response.firstName} {response.lastName}
                            {nameCounts[`${response.firstName?.trim().toLowerCase()} ${response.lastName?.trim().toLowerCase()}`] > 1 && (
                              <span title="Duplicate name" className="ml-2 text-yellow-500">
                                ⚠️
                              </span>
                            )}
                          </div>
                        </td>
                        <td className="py-3 px-4">
                          <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                            response.attending
                              ? 'bg-green-100 text-green-800'
                              : 'bg-red-100 text-red-800'
                          }`}>
                            {response.attending ? 'Attending' : 'Not Attending'}
                          </span>
                        </td>
                        <td className="py-3 px-4 text-gray-600">
                          {response.connectionType || '-'}
                        </td>
                        <td className="py-3 px-4 text-gray-600">
                          {response.guestCount}
                        </td>
                        <td className="py-3 px-4 text-gray-600">
                          {new Date(response.submittedAt).toLocaleDateString()}
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>

                {filteredResponses.length === 0 && (
                  <div className="text-center py-8">
                    <Users className="h-12 w-12 text-gray-300 mx-auto mb-4" />
                    <p className="text-gray-600">No responses found for the selected filter.</p>
                  </div>
                )}
              </div>
            </div>
          </div>
        )}
      </div>
      {showCustomizeConnections && (
        <div className="fixed inset-0 bg-black bg-opacity-30 flex items-center justify-center z-50">
          <div className="bg-white p-8 rounded shadow-lg max-w-2xl w-full">
            <h2 className="text-lg font-bold mb-4">Customize RSVP Form</h2>
            {/* TODO: Embed full RSVP customization form here, prefilled with currentWedding data, and allow saving changes */}
            <button className="btn-primary mt-4" onClick={() => setShowCustomizeConnections(false)}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default WeddingDashboard;
