import React, { useState } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';

const defaultConnectionOptions = [
  'Friend of the Bride',
  'Friend of the Groom',
  'Family of the Bride',
  'Family of the Groom',
  'College Friend',
  'Work Colleague',
  'Neighbor',
  'Other'
];

const CustomizeWeddingPage = () => {
  const { currentWedding } = useAuth();
  const navigate = useNavigate();
  const [form, setForm] = useState({
    brideName: currentWedding?.brideName || '',
    groomName: currentWedding?.groomName || '',
    welcomeMessage: currentWedding?.welcomeMessage || '',
    weddingDate: currentWedding?.weddingDate || '',
    ceremonyTime: currentWedding?.ceremonyTime || '',
    language: currentWedding?.language || 'en',
    connectionOptions: currentWedding?.connectionOptions || [
      'Friend of the Bride',
      'Friend of the Groom',
      'Family of the Bride',
      'Family of the Groom'
    ],
  });
  const [openSection, setOpenSection] = useState('form');

  const handleChange = (field, value) => {
    setForm(prev => ({ ...prev, [field]: value }));
  };

  const toggleConnectionOption = (option) => {
    setForm(prev => ({
      ...prev,
      connectionOptions: prev.connectionOptions.includes(option)
        ? prev.connectionOptions.filter(o => o !== option)
        : [...prev.connectionOptions, option]
    }));
  };

  return (
    <div className="min-h-screen p-8 bg-gray-50">
      <div className="max-w-3xl mx-auto bg-white rounded-lg shadow-lg p-8">
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-3xl font-bold text-center flex-1">Customize Your RSVP Form</h1>
          <button className="btn-secondary ml-4" onClick={() => navigate('/dashboard')}>
            Back to Dashboard
          </button>
        </div>
        {/* Accordion: RSVP Form Settings */}
        <div className="mb-4">
          <button className="w-full text-left py-3 px-4 bg-gray-100 rounded-t-lg font-semibold" onClick={() => setOpenSection(openSection === 'form' ? '' : 'form')}>
            RSVP Form Settings
          </button>
          {openSection === 'form' && (
            <div className="border border-t-0 rounded-b-lg p-4 space-y-4">
              <div>
                <label className="form-label">Bride's Name</label>
                <input
                  type="text"
                  className="form-input"
                  value={form.brideName}
                  onChange={e => handleChange('brideName', e.target.value)}
                  placeholder="Enter bride's name"
                />
              </div>
              <div>
                <label className="form-label">Groom's Name</label>
                <input
                  type="text"
                  className="form-input"
                  value={form.groomName}
                  onChange={e => handleChange('groomName', e.target.value)}
                  placeholder="Enter groom's name"
                />
              </div>
              <div>
                <label className="form-label">Welcome Sentence</label>
                <textarea
                  className="form-input"
                  value={form.welcomeMessage}
                  onChange={e => handleChange('welcomeMessage', e.target.value)}
                  placeholder="Enter a welcome message"
                />
              </div>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label className="form-label">Wedding Date</label>
                  <input
                    type="date"
                    className="form-input"
                    value={form.weddingDate}
                    onChange={e => handleChange('weddingDate', e.target.value)}
                  />
                </div>
                <div>
                  <label className="form-label">Ceremony Time</label>
                  <input
                    type="time"
                    className="form-input"
                    value={form.ceremonyTime}
                    onChange={e => handleChange('ceremonyTime', e.target.value)}
                  />
                </div>
              </div>
              <div>
                <label className="form-label">Primary Language</label>
                <select
                  className="form-select"
                  value={form.language}
                  onChange={e => handleChange('language', e.target.value)}
                >
                  <option value="en">English 🇺🇸</option>
                  <option value="he">Hebrew 🇮🇱</option>
                </select>
              </div>
            </div>
          )}
        </div>
        {/* Accordion: Connection Options */}
        <div className="mb-4">
          <button className="w-full text-left py-3 px-4 bg-gray-100 font-semibold" onClick={() => setOpenSection(openSection === 'connections' ? '' : 'connections')}>
            Connection Options
          </button>
          {openSection === 'connections' && (
            <div className="border border-t-0 rounded-b-lg p-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                {defaultConnectionOptions.map(option => (
                  <label key={option} className="flex items-center">
                    <input
                      type="checkbox"
                      checked={form.connectionOptions.includes(option)}
                      onChange={() => toggleConnectionOption(option)}
                      className="h-4 w-4 text-purple-600 focus:ring-purple-500 border-gray-300 rounded"
                    />
                    <span className="ml-2 text-sm text-gray-700">{option}</span>
                  </label>
                ))}
              </div>
            </div>
          )}
        </div>
        {/* Accordion: Visual Style */}
        <div className="mb-8">
          <button className="w-full text-left py-3 px-4 bg-gray-100 font-semibold" onClick={() => setOpenSection(openSection === 'style' ? '' : 'style')}>
            Visual Style
          </button>
          {openSection === 'style' && (
            <div className="border border-t-0 rounded-b-lg p-4">
              <div className="text-gray-500">Visual style customization goes here...</div>
            </div>
          )}
        </div>
        {/* Save Button */}
        <div className="flex justify-end">
          <button className="btn-primary">Save Changes</button>
        </div>
      </div>
    </div>
  );
};

export default CustomizeWeddingPage;
