import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { 
  Users, 
  Calendar, 
  MapPin, 
  Settings, 
  Download, 
  LogOut,
  Heart,
  TrendingUp,
  UserCheck,
  UserX,
  BarChart3,
  Edit
} from 'lucide-react';
import axios from 'axios';
import toast from 'react-hot-toast';

const WeddingDashboard = () => {
  const { currentWedding, logoutFromWedding } = useAuth();
  const [stats, setStats] = useState(null);
  const [responses, setResponses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('overview');
  const [filter, setFilter] = useState('all');

  useEffect(() => {
    if (currentWedding) {
      fetchData();
    }
  }, [currentWedding]);

  const fetchData = async () => {
    try {
      const [statsResponse, responsesResponse] = await Promise.all([
        axios.get(`/api/rsvp/${currentWedding.weddingId}/statistics`),
        axios.get(`/api/rsvp/${currentWedding.weddingId}/responses`)
      ]);
      
      setStats(statsResponse.data.statistics);
      setResponses(responsesResponse.data.responses);
    } catch (error) {
      toast.error('Failed to fetch wedding data');
    } finally {
      setLoading(false);
    }
  };

  const handleExport = async (filterType = 'all') => {
    try {
      const response = await axios.get(`/api/rsvp/${currentWedding.weddingId}/export?filter=${filterType}`, {
        responseType: 'blob'
      });
      
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `guest-list-${currentWedding.weddingId}-${filterType}.csv`);
      document.body.appendChild(link);
      link.click();
      link.remove();
      
      toast.success('Guest list exported successfully!');
    } catch (error) {
      toast.error('Failed to export guest list');
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-purple-600"></div>
      </div>
    );
  }

  const filteredResponses = responses.filter(response => {
    if (filter === 'attending') return response.attending;
    if (filter === 'not-attending') return !response.attending;
    return true;
  });

  return (
    <div className="min-h-screen p-6">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="flex justify-between items-center mb-8">
          <div>
            <h1 className="text-4xl font-bold text-gradient mb-2">
              Wedding Dashboard
            </h1>
            <p className="text-gray-600">
              {currentWedding.brideName} & {currentWedding.groomName}
            </p>
          </div>
          <div className="flex space-x-4">
            <button
              onClick={() => setActiveTab('settings')}
              className="btn-secondary flex items-center"
            >
              <Settings className="h-5 w-5 mr-2" />
              Settings
            </button>
            <button
              onClick={logoutFromWedding}
              className="btn-secondary flex items-center"
            >
              <LogOut className="h-5 w-5 mr-2" />
              Logout
            </button>
          </div>
        </div>

        {/* Wedding Info */}
        <div className="card mb-8">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <div className="flex items-center">
              <Calendar className="h-6 w-6 text-purple-600 mr-3" />
              <div>
                <p className="font-medium text-gray-900">Wedding Date</p>
                <p className="text-gray-600">{formatDate(currentWedding.weddingDate)}</p>
              </div>
            </div>
            <div className="flex items-center">
              <MapPin className="h-6 w-6 text-purple-600 mr-3" />
              <div>
                <p className="font-medium text-gray-900">Venue</p>
                <p className="text-gray-600">{currentWedding.venueDetails || 'Not specified'}</p>
              </div>
            </div>
            <div className="flex items-center">
              <Users className="h-6 w-6 text-purple-600 mr-3" />
              <div>
                <p className="font-medium text-gray-900">Language</p>
                <p className="text-gray-600">{currentWedding.language === 'he' ? 'Hebrew' : 'English'}</p>
              </div>
            </div>
          </div>
        </div>

        {/* Tab Navigation */}
        <div className="flex space-x-1 bg-gray-100 rounded-lg p-1 mb-8">
          <button
            onClick={() => setActiveTab('overview')}
            className={`flex-1 py-2 px-4 rounded-md text-sm font-medium transition-all duration-200 ${
              activeTab === 'overview'
                ? 'bg-white text-purple-600 shadow-sm'
                : 'text-gray-600 hover:text-gray-900'
            }`}
          >
            <BarChart3 className="h-4 w-4 inline mr-2" />
            Overview
          </button>
          <button
            onClick={() => setActiveTab('guest-list')}
            className={`flex-1 py-2 px-4 rounded-md text-sm font-medium transition-all duration-200 ${
              activeTab === 'guest-list'
                ? 'bg-white text-purple-600 shadow-sm'
                : 'text-gray-600 hover:text-gray-900'
            }`}
          >
            <Users className="h-4 w-4 inline mr-2" />
            Guest List
          </button>
        </div>

        {/* Overview Tab */}
        {activeTab === 'overview' && (
          <div className="space-y-8">
            {/* Stats Cards */}
            <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
              <div className="stat-card">
                <div className="stat-number">{stats?.total || 0}</div>
                <div className="stat-label">Total RSVPs</div>
              </div>
              <div className="stat-card">
                <div className="stat-number">{stats?.attending || 0}</div>
                <div className="stat-label">Attending</div>
              </div>
              <div className="stat-card">
                <div className="stat-number">{stats?.notAttending || 0}</div>
                <div className="stat-label">Not Attending</div>
              </div>
              <div className="stat-card">
                <div className="stat-number">{stats?.guestCount || 0}</div>
                <div className="stat-label">Total Guests</div>
              </div>
            </div>

            {/* Connection Breakdown */}
            {stats?.connectionBreakdown && Object.keys(stats.connectionBreakdown).length > 0 && (
              <div className="card">
                <h3 className="text-xl font-semibold text-gray-900 mb-4">Connection Breakdown</h3>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                  {Object.entries(stats.connectionBreakdown).map(([connection, count]) => (
                    <div key={connection} className="bg-gray-50 rounded-lg p-4">
                      <div className="flex justify-between items-center">
                        <span className="font-medium text-gray-900">{connection}</span>
                        <span className="text-2xl font-bold text-purple-600">{count}</span>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}

            {/* Export Options */}
            <div className="card">
              <h3 className="text-xl font-semibold text-gray-900 mb-4">Export Guest Lists</h3>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <button
                  onClick={() => handleExport('all')}
                  className="btn-secondary flex items-center justify-center"
                >
                  <Download className="h-5 w-5 mr-2" />
                  Export All
                </button>
                <button
                  onClick={() => handleExport('attending')}
                  className="btn-secondary flex items-center justify-center"
                >
                  <UserCheck className="h-5 w-5 mr-2" />
                  Export Attending
                </button>
                <button
                  onClick={() => handleExport('not-attending')}
                  className="btn-secondary flex items-center justify-center"
                >
                  <UserX className="h-5 w-5 mr-2" />
                  Export Not Attending
                </button>
              </div>
            </div>
          </div>
        )}

        {/* Guest List Tab */}
        {activeTab === 'guest-list' && (
          <div className="space-y-6">
            {/* Filter Controls */}
            <div className="card">
              <div className="flex justify-between items-center">
                <h3 className="text-xl font-semibold text-gray-900">Guest List</h3>
                <div className="flex space-x-2">
                  <select
                    value={filter}
                    onChange={(e) => setFilter(e.target.value)}
                    className="form-select w-auto"
                  >
                    <option value="all">All Responses</option>
                    <option value="attending">Attending</option>
                    <option value="not-attending">Not Attending</option>
                  </select>
                  <button
                    onClick={() => handleExport(filter)}
                    className="btn-secondary flex items-center"
                  >
                    <Download className="h-5 w-5 mr-2" />
                    Export
                  </button>
                </div>
              </div>
            </div>

            {/* Guest List Table */}
            <div className="card">
              <div className="overflow-x-auto">
                <table className="w-full">
                  <thead>
                    <tr className="border-b border-gray-200">
                      <th className="text-left py-3 px-4 font-semibold text-gray-900">Name</th>
                      <th className="text-left py-3 px-4 font-semibold text-gray-900">Status</th>
                      <th className="text-left py-3 px-4 font-semibold text-gray-900">Connection</th>
                      <th className="text-left py-3 px-4 font-semibold text-gray-900">Guests</th>
                      <th className="text-left py-3 px-4 font-semibold text-gray-900">Submitted</th>
                    </tr>
                  </thead>
                  <tbody>
                    {filteredResponses.map((response) => (
                      <tr key={response.id} className="border-b border-gray-100 hover:bg-gray-50">
                        <td className="py-3 px-4">
                          <div className="font-medium text-gray-900">
                            {response.firstName} {response.lastName}
                          </div>
                        </td>
                        <td className="py-3 px-4">
                          <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                            response.attending
                              ? 'bg-green-100 text-green-800'
                              : 'bg-red-100 text-red-800'
                          }`}>
                            {response.attending ? 'Attending' : 'Not Attending'}
                          </span>
                        </td>
                        <td className="py-3 px-4 text-gray-600">
                          {response.connectionType || '-'}
                        </td>
                        <td className="py-3 px-4 text-gray-600">
                          {response.guestCount}
                        </td>
                        <td className="py-3 px-4 text-gray-600">
                          {new Date(response.submittedAt).toLocaleDateString()}
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
                
                {filteredResponses.length === 0 && (
                  <div className="text-center py-8">
                    <Users className="h-12 w-12 text-gray-300 mx-auto mb-4" />
                    <p className="text-gray-600">No responses found for the selected filter.</p>
                  </div>
                )}
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default WeddingDashboard;
